import json
import re

def parse_github_issues(issues_json_string):
    issues_data = json.loads(issues_json_string)
    parsed_tools = []

    for issue in issues_data[:5]:  # Process the first 5 issues
        title = issue.get('title', 'N/A')
        body = issue.get('body', '')

        # Extract URL
        url_match = re.search(r"### 1\. Website Link\r\n-\s*(https?://[^\s]+|[^\s]+)", body)
        url = url_match.group(1).strip() if url_match else 'N/A'

        # Ensure "https://" is present if the original URL had it, but don't add it if it's just a domain
        if "https://" in (url_match.group(1) if url_match else "") and not url.startswith("https://"):
             # This case should ideally not happen if regex is correct, but as a safeguard
             pass # url already correctly captured by regex
        elif not url.startswith("https://") and "." in url and "https://" not in (url_match.group(1) if url_match else ""):
            # Heuristic: if it has a dot, it's likely a domain, but wasn't specified with https
            # For this task, we only add https:// if it was explicitly in the body.
            pass


        # Extract Category
        category_match = re.search(r"### 2\. Category \(Optional\)\r\n-\s*([^\r\n]+)", body)
        category = category_match.group(1).strip() if category_match else ''

        if not category or category == "What category best describes this tool?":
            category = "Xtras"

        parsed_tools.append({
            "title": title,
            "url": url,
            "category": category
        })

    return parsed_tools

